function MintButton(props) {

    const getMetadataHash = async() => {

        let metadataJSON = {
            "description": "Graffiti Collection",
            "image": props.imageURI,
            "name": props.name,
            "attributes": [
                {
                    "trait_type": "Artist",
                    "value": props.artist
                }
            ]
        }
    
        let res = await pinata.pinJSONToIPFS(metadataJSON);
    
        return res.IpfsHash;
    }

    const mint = async() => {

        if(props.provider){

            try{
                //Step 1: Submit data to ipfs 
                let metadataHash = await getMetadataHash();
                console.log("metadataHash: ", metadataHash);

                let tokenURI = `ipfs://${metadataHash}`

                //Step 2: Call mint function on NFT contract
                const signer = props.provider.getSigner()
                
                var contract = new ethers.Contract(contractAddress,ABI, signer);
                var sendPromise = contract.createToken(tokenURI);

                sendPromise.then(function(transaction){
                console.log(transaction);
                });

            }
            catch(error){
                console.log(error);
            }
        }
        else{
            console.log("Log in to mint!")
        }

    }

    return (
        <div>
            <button onClick={() => mint()}>Mint</button>
        </div>
    );
}

export default MintButton;